import React, { useEffect, useState } from 'react';
import { useStateValue } from '../state'
import { List, ListItem, ListItemText, ListSubheader } from '@material-ui/core'
import '../css/Dashboard.scss'
import Button from '@material-ui/core/Button'

const getRanking = (username, setRanking) => {
    fetch(`${window.location.origin}/getranking?user=${username}`)
    .then(response => {
        if(!response.ok) console.log(response.statusText)
        else response.json().then(setRanking)
    }).catch(console.log)
}

const RankingPage = () => {

  const [{ username }, ] = useStateValue()
  const [ranking, setRanking] = useState([])

  useEffect(() => getRanking(username, setRanking), [username])

  return (
    <div className="RankingPage">
        <List subheader={ <ListSubheader>Monthly Ranking</ListSubheader> } >
            {ranking.map((person, i) =>
                <ListItem key={i}>
                    <ListItemText primary={person.username} />
                </ListItem>
            )}
        </List>
    </div>
  );
}

export default RankingPage;
